# extracted from unzipped excel sheet <G:\temp\export-copy>
$dataMashup = @'

'@

$dataMashup -replace '[a-zA-Z0-9]',''
$maybeItems = $dataMashup -split '/' | CountOf
$cachedNaiveConvert ??= ([convert]::FromBase64String($dataMashup)).ForEach({[Text.Rune]$_})|Join-String { $_| fcc } | %{ $_ -replace '&quot;', '"' -replace '‚ê†', ' ' -replace [regex]::Escape('<'), "`n<" }

$cachedNaiveConvert

function ColorizeDelims { process {
    $_ -replace '\+',
        "${bg:#ff9a9a}+${bg:clear}" -replace '/',
        "${bg:#9a9aff}/${bg:clear}" -replace '=',
        "${bg:#9aff9a}/${bg:clear}"
} }

$dataMashup | ColorizeDelims